import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { deleteBook, fetchBooks } from "./BookSlice";

const BooksView = () => {
  //const book = useSelector((state) => state.booksReducer.books);
  const dispatch = useDispatch();
  const  {isLoading,books,error}  = useSelector((state) => {
    console.log(state.booksReducer);
    return state.books;
  });
  useEffect(() => {
    dispatch(fetchBooks());
  }, []);

  const handleDeleteBook = (id) => {
    dispatch(deleteBook(id));
  };
  return (
    <div>
      {isLoading && <h3>Loading</h3>}
      {error && <h3>Error</h3>}
      <h2>List of Books</h2>
      <table>
        <thead>
          <tr>
            {/* <th>ID</th> */}
            <th>Title</th>
            <th>Author</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>

          <Link to="/add-books">
            <button>Add Books</button>
          </Link>
        </tbody>
      </table>
    </div>
  );
};

export default BooksView;
