import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { deleteBook, fetchBooks } from "./BookSlice";

const BooksView = () => {
  const books = useSelector((state) => state.booksReducer.books);
  const dispatch = useDispatch();
  // const  {isLoading,book,error}  = useSelector((state) => {
  //   console.log(state.booksReducer);
  //   return state.books;
  // });
  useEffect(() => {
    dispatch(fetchBooks());
  }, []);

  const handleDeleteBook = (id) => {
    dispatch(deleteBook(id));
  };
  return (
    <div>
      {isLoading && <h3>Loading</h3>}
      {error && <h3>Error</h3>}
      <h2>List of Books</h2>
      <table>
        <thead>
          <tr>
            {/* <th>ID</th> */}
            <th>Title</th>
            <th>Author</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {books && books.map(({id,name,author,price})=>(
            <tr key={id}>
            {/* <td>{id}</td> */}
            <td>{name}</td>
            <td>{author}</td>
            <td>{price}</td>
            <td>
              <Link to="/edit-books" state={{ id, name, author }}>
                <button>Edit</button>
              </Link>
              <button
                onClick={() => {
                  handleDeleteBook(id);
                }}
              >
                Delete
              </button>
            </td>
          </tr>
          ))}
          {books &&
            books.map((book) => {
              const { id, title, author, price } = book;
              return (
                <tr key={id}>
                  {/* <td>{id}</td> */}
                  <td>{title}</td>
                  <td>{author}</td>
                  <td>{price}</td>
                  <td>
                    <Link to="/edit-books" state={{ id, title, author }}>
                      <button>Edit</button>
                    </Link>
                    <button
                      onClick={() => {
                        handleDeleteBook(id);
                      }}
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              );
            })}
          <Link to="/add-books">
            <button>Add Books</button>
          </Link>
        </tbody>
      </table>
    </div>
  );
};

export default BooksView;
