import React, { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";
import { deleteBook, fetchBooks } from "./BookSlice";

const BooksView = () => {
  const books = useSelector((state) => state.booksReducer.books);
  console.log(books);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(fetchBooks());
  }, []);
  // const { isLoading, books, error } = useSelector((state) => {
  //   console.log(state.booksReducer);
  //   return state.booksReducer;
  // });
  const handleDeleteBook = (id) => {
    dispatch(deleteBook(id));
  };
  return (
    <div>
      {/* {isLoading && <h3>Loading</h3>}
      {error && <h3>Error</h3>} */}
      <h2>List of Books</h2>
      <table>
        <thead>
          <tr>
            {/* <th>ID</th> */}
            <th>Name</th>
            <th>Author</th>
            <th>Price</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody >
          {books &&
            books.map(({ id, name, author, price }) => (
              <tr key={id}>
                {/* <td>{id}</td> */}
                <td>{name}</td>
                <td>{author}</td>
                <td>{price}</td>
                <td>
                  <Link to="/edit-books" state={{ id, name, author, price }}>
                    <button>Edit</button>
                  </Link>
                  <button onClick={() => handleDeleteBook}>
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          <Link to="/add-books">
            <button>Add Books</button>
          </Link>
        </tbody>
      </table>
    </div>
  );
};

export default BooksView;
